import React, { useState } from "react";
import axios from "axios";

function UploadAvatar() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState("");
  const [message, setMessage] = useState("");

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setPreview(URL.createObjectURL(selectedFile));
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return setMessage("❌ Vui lòng chọn ảnh!");

    const token = localStorage.getItem("token");
    if (!token) return setMessage("❌ Bạn chưa đăng nhập");

    const formData = new FormData();
    formData.append("avatar", file);

    try {
      const res = await axios.post(
        "http://localhost:3000/api/users/upload-avatar",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setMessage("✅ " + res.data.message);
      setPreview(res.data.avatar);
      setFile(null);
    } catch (err) {
      setMessage("❌ " + (err.response?.data?.message || "Lỗi upload ảnh!"));
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={styles.title}>Cập nhật ảnh đại diện</h2>
        <form onSubmit={handleUpload}>
          <input
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            style={{ width: "100%", boxSizing: "border-box", marginBottom: "15px" }}
          />
          {preview && <img src={preview} alt="preview" style={styles.preview} />}
          <button type="submit" style={styles.btnSubmit}>Tải lên</button>
        </form>
        {message && <p style={styles.message}>{message}</p>}
      </div>
    </div>
  );
}

const styles = {
  container: { display: "flex", justifyContent: "center", alignItems: "center", height: "100vh", background: "#e2e8f0" },
  card: { background: "#fff", padding: "30px", borderRadius: "20px", width: "380px", textAlign: "center", boxShadow: "0 8px 20px rgba(0,0,0,0.1)" },
  title: { fontSize: "24px", marginBottom: "20px" },
  preview: { width: "120px", height: "120px", borderRadius: "50%", objectFit: "cover", margin: "10px auto" },
  btnSubmit: { width: "100%", padding: "12px", background: "#4f46e5", color: "#fff", border: "none", borderRadius: "12px", fontSize: "16px", cursor: "pointer" },
  message: { marginTop: "15px", fontSize: "14px", color: "green" },
};

export default UploadAvatar;
