import React, { useEffect, useState } from "react";
import axios from "axios";

function AdminDashboard() {
  const [users, setUsers] = useState([]);
  const [editUser, setEditUser] = useState(null);
  const [form, setForm] = useState({ name: "", email: "" });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // üîπ L·∫•y token t·ª´ localStorage
  const token = localStorage.getItem("token");

  // ‚úÖ T·∫£i danh s√°ch user khi load trang
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:3000/api/users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data);
    } catch (err) {
      console.error("L·ªói t·∫£i danh s√°ch:", err);
      setMessage("‚ö†Ô∏è Kh√¥ng th·ªÉ t·∫£i danh s√°ch ng∆∞·ªùi d√πng!");
    }
  };

  // ‚úÖ X√ìA user
  const handleDelete = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a ng∆∞·ªùi d√πng n√†y kh√¥ng?")) return;
    try {
      const res = await axios.delete(`http://localhost:3000/api/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers((prev) => prev.filter((u) => u._id !== id));
      setMessage("‚úÖ " + res.data.message);
    } catch (err) {
      console.error("L·ªói khi x√≥a:", err);
      setMessage("‚ùå X√≥a th·∫•t b·∫°i!");
    }
  };

  // ‚úÖ M·ªü form ch·ªânh s·ª≠a
  const handleEdit = (user) => {
    setEditUser(user);
    setForm({ name: user.name, email: user.email });
  };

  // ‚úÖ L∆∞u c·∫≠p nh·∫≠t user
  const handleUpdate = async () => {
    setLoading(true);
    try {
      const res = await axios.put(
        `http://localhost:3000/api/users/${editUser._id}`,
        form,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUsers((prev) =>
        prev.map((u) => (u._id === editUser._id ? res.data : u))
      );
      setEditUser(null);
      setMessage("‚úÖ C·∫≠p nh·∫≠t th√†nh c√¥ng!");
    } catch (err) {
      console.error("C·∫≠p nh·∫≠t l·ªói:", err);
      setMessage("‚ùå C·∫≠p nh·∫≠t th·∫•t b·∫°i!");
    }
    setLoading(false);
  };

  // ‚úÖ ƒêƒÉng xu·∫•t admin
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    window.location.href = "/";
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h2 style={styles.title}>üìã B·∫£ng ƒëi·ªÅu khi·ªÉn Admin</h2>
        <button style={styles.logoutBtn} onClick={handleLogout}>
          üö™ ƒêƒÉng xu·∫•t
        </button>
      </div>

      <div style={styles.cardContainer}>
        {users.length > 0 ? (
          users.map((user) => (
            <div key={user._id} style={styles.card}>
              <h3 style={styles.name}>{user.name}</h3>
              <p style={styles.email}>{user.email}</p>
              <p style={styles.role}>
                Quy·ªÅn:{" "}
                <b
                  style={{
                    color: user.role === "admin" ? "#e67e22" : "#16a34a",
                  }}
                >
                  {user.role}
                </b>
              </p>
              <div style={styles.actions}>
                <button style={styles.editBtn} onClick={() => handleEdit(user)}>
                  ‚úèÔ∏è S·ª≠a
                </button>
                <button
                  style={styles.deleteBtn}
                  onClick={() => handleDelete(user._id)}
                >
                  üóë X√≥a
                </button>
              </div>
            </div>
          ))
        ) : (
          <p style={{ textAlign: "center" }}>Kh√¥ng c√≥ ng∆∞·ªùi d√πng n√†o.</p>
        )}
      </div>

      {editUser && (
        <div style={styles.editForm}>
          <h3 style={styles.formTitle}>üõ† S·ª≠a th√¥ng tin ng∆∞·ªùi d√πng</h3>
          <input
            style={styles.input}
            type="text"
            placeholder="T√™n"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
          />
          <input
            style={styles.input}
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
          />
          <div>
            <button style={styles.saveBtn} onClick={handleUpdate} disabled={loading}>
              {loading ? "‚è≥ ƒêang l∆∞u..." : "üíæ L∆∞u"}
            </button>
            <button style={styles.cancelBtn} onClick={() => setEditUser(null)}>
              ‚ùå H·ªßy
            </button>
          </div>
        </div>
      )}

      {message && <p style={styles.message}>{message}</p>}
    </div>
  );
}

const styles = {
  container: {
    padding: "30px",
    maxWidth: "1000px",
    margin: "auto",
    fontFamily: "Inter, sans-serif",
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "25px",
  },
  title: {
    color: "#1f2937",
    fontSize: "24px",
    fontWeight: "700",
  },
  logoutBtn: {
    background: "linear-gradient(135deg, #ef4444, #f97316)",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    padding: "10px 16px",
    fontWeight: "600",
    cursor: "pointer",
    boxShadow: "0 4px 12px rgba(239,68,68,0.3)",
  },
  cardContainer: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "20px",
  },
  card: {
    backgroundColor: "#f9fafb",
    borderRadius: "14px",
    padding: "20px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
  },
  name: { color: "#111827", fontSize: "18px", marginBottom: "5px" },
  email: { color: "#6b7280", fontSize: "14px", marginBottom: "5px" },
  role: { fontSize: "14px", marginBottom: "10px" },
  actions: { display: "flex", justifyContent: "space-between" },
  editBtn: {
    backgroundColor: "#f59e0b",
    border: "none",
    borderRadius: "8px",
    padding: "6px 12px",
    color: "#fff",
    fontWeight: "600",
    cursor: "pointer",
  },
  deleteBtn: {
    backgroundColor: "#ef4444",
    border: "none",
    borderRadius: "8px",
    padding: "6px 12px",
    color: "#fff",
    fontWeight: "600",
    cursor: "pointer",
  },
  editForm: {
    marginTop: "40px",
    padding: "20px",
    backgroundColor: "#fff",
    borderRadius: "12px",
    boxShadow: "0 2px 10px rgba(0,0,0,0.15)",
    textAlign: "center",
  },
  input: {
    width: "100%",
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    marginBottom: "10px",
  },
  saveBtn: {
    backgroundColor: "#10b981",
    border: "none",
    borderRadius: "8px",
    padding: "8px 14px",
    marginRight: "10px",
    color: "#fff",
    fontWeight: "bold",
  },
  cancelBtn: {
    backgroundColor: "#9ca3af",
    border: "none",
    borderRadius: "8px",
    padding: "8px 14px",
    color: "#fff",
    fontWeight: "bold",
  },
  message: {
    textAlign: "center",
    marginTop: "20px",
    fontWeight: "600",
    color: "#16a34a",
  },
};

export default AdminDashboard;
