import React, { useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function ResetPassword() {
  const { token } = useParams();
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`http://localhost:3000/api/reset-password/${token}`, { password });
      setMessage(res.data.message);
    } catch {
      setMessage("‚ùå Token kh√¥ng h·ª£p l·ªá!");
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>üîí ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="password"
          placeholder="Nh·∫≠p m·∫≠t kh·∫©u m·ªõi"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={styles.input}
        />
        <button type="submit" style={styles.button}>C·∫≠p nh·∫≠t m·∫≠t kh·∫©u</button>
      </form>
      {message && <p style={{ textAlign: "center", marginTop: "10px" }}>{message}</p>}
    </div>
  );
}

const styles = {
  container: {
    background: "#fff",
    padding: "40px",
    margin: "60px auto",
    borderRadius: "16px",
    boxShadow: "0 8px 20px rgba(0,0,0,0.1)",
    maxWidth: "400px",
  },
  title: { textAlign: "center", color: "#10b981" },
  input: {
    width: "100%",
    padding: "14px",
    borderRadius: "10px",
    border: "1px solid #d1d5db",
    marginBottom: "15px",
  },
  button: {
    width: "100%",
    padding: "14px",
    background: "linear-gradient(135deg, #059669, #10b981)",
    color: "#fff",
    border: "none",
    borderRadius: "12px",
  },
};

export default ResetPassword;
