import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function UploadAvatar() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();
  const fileInputRef = useRef(null); // Ref để reset input file

  // Xử lý khi chọn file
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (!selectedFile) return;
    setFile(selectedFile);

    // Tạo URL preview
    const objectUrl = URL.createObjectURL(selectedFile);
    setPreview(objectUrl);
  };

  // Upload avatar
  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) return setMessage("❌ Vui lòng chọn ảnh!");

    const token = localStorage.getItem("token");
    if (!token) return setMessage("❌ Bạn chưa đăng nhập");

    const formData = new FormData();
    formData.append("avatar", file);

    try {
      const res = await axios.post(
        "http://localhost:3000/api/upload/upload-avatar",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setMessage("✅ " + res.data.message);
      setPreview(res.data.avatar);
      setFile(null);

      // Reset input file trong DOM
      if (fileInputRef.current) fileInputRef.current.value = "";

      // Chuyển về profile sau 1 giây
      setTimeout(() => navigate("/profile"), 1000);

    } catch (err) {
      setMessage("❌ " + (err.response?.data?.message || "Lỗi upload ảnh!"));
    }
  };

  // Cleanup object URL khi component unmount hoặc preview thay đổi
  useEffect(() => {
    return () => {
      if (preview) URL.revokeObjectURL(preview);
    };
  }, [preview]);

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={styles.title}>Cập nhật ảnh đại diện</h2>

        <form onSubmit={handleUpload}>
          <input
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            ref={fileInputRef}
            style={{ width: "100%", boxSizing: "border-box", marginBottom: "15px" }}
          />
          {preview && <img src={preview} alt="preview" style={styles.preview} />}
          <button type="submit" style={styles.btnSubmit}>Tải lên</button>
        </form>

        {message && <p style={styles.message}>{message}</p>}
      </div>
    </div>
  );
}

const styles = {
  container: {
    background: "linear-gradient(135deg,#c2e9fb,#a1c4fd)",
    height: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  card: {
    background: "#fff",
    padding: "40px",
    borderRadius: "20px",
    width: "380px",
    textAlign: "center",
    boxShadow: "0 8px 25px rgba(0,0,0,0.15)",
  },
  title: { fontSize: "26px", marginBottom: "25px" },
  preview: {
    width: "120px",
    height: "120px",
    borderRadius: "50%",
    objectFit: "cover",
    margin: "15px auto",
  },
  message: { fontSize: "14px", marginTop: "15px" },
  btnSubmit: {
    width: "100%",
    padding: "12px 14px",
    background: "linear-gradient(135deg,#4facfe,#00f2fe)",
    color: "white",
    border: "none",
    borderRadius: "12px",
    fontSize: "16px",
    cursor: "pointer",
    fontWeight: "600",
    marginTop: "15px",
  },
};

export default UploadAvatar;
