import React, { useState } from "react"; // ‚ùå B·ªè useEffect
import { Routes, Route, useNavigate, Navigate } from "react-router-dom";
// ‚ùå B·ªè jwtDecode
import { useDispatch, useSelector } from "react-redux"; // ‚úÖ Th√™m useSelector
import {
  // ‚ùå B·ªè loginSuccess
  logout as logoutAction,
} from "./redux/authSlice";

import api from "./api";
import Login from "./Login";
import Signup from "./Signup";
import UserList from "./UserList";
import AddUser from "./AddUser";
import Profile from "./ProfileUser";
import ForgotPassword from "./ForgotPassword";
import ResetPassword from "./ResetPassword";
import UploadAvatar from "./UploadAvatar";
import AdminLogs from "./AdminLogs";
import ProtectedRoute from "./ProtectedRoute";
import RoleRoute from "./RoleRoute";

function App() {
  const [refresh, setRefresh] = useState(false);
  // ‚ùå X√≥a state: const [currentUser, setCurrentUser] = useState(null);
  // ‚ùå X√≥a state: const [loading, setLoading] = useState(true);

  const navigate = useNavigate();
  const dispatch = useDispatch();

  // ‚úÖ L·∫•y user tr·ª±c ti·∫øp t·ª´ Redux store
  const { user: currentUser } = useSelector((state) => state.auth);

  const handleAdd = () => setRefresh(!refresh);

  // üßπ ƒêƒÉng xu·∫•t (ƒê√£ d·ªçn d·∫πp)
  const handleLogout = async () => {
    const refreshToken = localStorage.getItem("refreshToken");
    try {
      if (refreshToken) await api.post("/auth/logout", { refreshToken });
    } catch (err) {
      console.error("L·ªói khi logout:", err);
    } finally {
      // ‚ùå X√≥a: ["accessToken", ...].forEach(...)
      // ‚ùå X√≥a: setCurrentUser(null);
      
      // ‚úÖ Ch·ªâ c·∫ßn dispatch v√† navigate
      dispatch(logoutAction());
      navigate("/");
    }
  };

  // ‚ùå X√≥a: To√†n b·ªô kh·ªëi `useEffect` t·∫£i user.
  // `preloadedState` trong `store.js` ƒë√£ l√†m vi·ªác n√†y r·ªìi.

  // ‚ùå X√≥a: Kh·ªëi `if (loading) ...`

  return (
    <Routes>
      {/* --- C√¥ng khai --- */}
      {/* ‚ùå X√≥a prop `setCurrentUser` */}
      <Route path="/" element={<Login />} />
      <Route path="/signup" element={<Signup />} />
      <Route path="/forgot-password" element={<ForgotPassword />} />
      <Route path="/reset-password/:token" element={<ResetPassword />} />

      {/* --- Ng∆∞·ªùi d√πng ƒë√£ login --- */}
      {/* ‚ùå X√≥a prop `currentUser` v√† `loading` */}
      <Route
        path="/profile"
        element={
          <ProtectedRoute>
            <Profile currentUser={currentUser} onLogout={handleLogout} />
          </ProtectedRoute>
        }
      />
      <Route
        path="/upload-avatar"
        element={
          <ProtectedRoute>
            <UploadAvatar />
          </ProtectedRoute>
        }
      />

      {/* --- Dashboard cho admin & moderator --- */}
      {/* ‚ùå X√≥a prop `currentUser` v√† `loading` */}
      <Route
        path="/dashboard"
        element={
          <RoleRoute allowedRoles={["admin", "moderator"]}>
            <div style={styles.container}>
              <div style={styles.header}>
                {/* `currentUser` ·ªü ƒë√¢y ƒë∆∞·ª£c l·∫•y t·ª´ useSelector ·ªü tr√™n */}
                {currentUser && (
                  <div style={styles.userInfo}>
                    <span>
                      Xin ch√†o, <b>{currentUser.name || currentUser.email}</b> (
                      {currentUser.role})
                    </span>

                    {currentUser.role === "admin" && (
                      <button
                        style={styles.logButton}
                        onClick={() => navigate("/logs")}
                      >
                        Nh·∫≠t k√Ω
                      </button>
                    )}

                    <button
                      style={styles.profileButton}
                      onClick={() => navigate("/profile")}
                    >
                      Trang c√° nh√¢n
                    </button>

                    <button
                      style={styles.logoutButton}
                      onClick={handleLogout}
                    >
                      ƒêƒÉng xu·∫•t
                    </button>
                  </div>
                )}

                <h1 style={styles.title}>
                  {currentUser?.role === "admin" ? (
                    <>
                      Qu·∫£n l√Ω ng∆∞·ªùi d√πng <br /> (Admin)
                    </>
                  ) : (
                    <>
                      B·∫£ng ƒëi·ªÅu khi·ªÉn <br /> (Moderator)
                    </>
                  )}
                </h1>
              </div>

              {currentUser?.role === "admin" && <AddUser onAdd={handleAdd} />}
              <UserList key={refresh} canEdit={currentUser?.role === "admin"} />
            </div>
          </RoleRoute>
        }
      />

      {/* --- Trang log cho Admin --- */}
      {/* ‚ùå X√≥a prop `currentUser` v√† `loading` */}
      <Route
        path="/logs"
        element={
          <RoleRoute allowedRoles={["admin"]}>
            <AdminLogs />
          </RoleRoute>
        }
      />

      {/* --- Trang m·∫∑c ƒë·ªãnh --- */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
}

// --- üé® STYLE ---
// (Gi·ªØ nguy√™n style)
const styles = {
  container: {
    position: "relative",
    fontFamily: "'Inter', sans-serif",
    padding: "30px",
  },
  header: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    position: "relative",
    marginBottom: "25px",
  },
  title: {
    textAlign: "center",
    fontSize: "28px",
    fontWeight: "700",
    marginTop: "50px",
  },
  userInfo: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    gap: "10px",
    position: "absolute",
    top: "0",
    right: "20px",
  },
  logButton: {
    background: "linear-gradient(to right, #059669, #34d399)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px",
    padding: "10px 18px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
    transition: "all 0.25s ease",
  },
  profileButton: {
    background: "linear-gradient(to right, #2563eb, #60a5fa)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px",
    padding: "10px 18px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
    transition: "all 0.25s ease",
  },
  logoutButton: {
    background: "linear-gradient(to right, #b91c1c, #f87171)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px",
    padding: "10px 20px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    transition: "all 0.25s ease",
  },
};

export default App;
