import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

function Login({ setCurrentUser }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:3000/api/auth/login", {
        email,
        password,
      });

      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(res.data.user));
      setCurrentUser(res.data.user);
      setMessage(res.data.message);

      setTimeout(() => {
        if (res.data.user.role === "admin") {
          navigate("/dashboard");
        } else {
          navigate("/profile");
        }
      }, 600);
    } catch (err) {
      setMessage(err.response?.data?.message || "❌ Lỗi đăng nhập");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={styles.title}>Đăng nhập tài khoản</h2>

        <form onSubmit={handleLogin} style={{ width: "100%" }}>
          {/* Email */}
          <input
            type="email"
            placeholder="Nhập email"
            className="input-field"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          {/* Mật khẩu + con mắt */}
          <div style={styles.inputWrapper}>
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Nhập mật khẩu"
              className="input-field"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{ paddingRight: "44px" }}
            />
            <button
              type="button"
              aria-label={showPassword ? "Ẩn mật khẩu" : "Hiện mật khẩu"}
              onClick={() => setShowPassword((s) => !s)}
              style={styles.iconButton}
            >
              {showPassword ? (
                // Eye-off SVG
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                  <path d="M3 3l18 18" stroke="#475569" strokeWidth="1.8" strokeLinecap="round" />
                  <path d="M10.5 10.5a3 3 0 0 0 4.5 4.5" stroke="#475569" strokeWidth="1.8" strokeLinecap="round" />
                </svg>
              ) : (
                // Eye SVG
                <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
                  <path
                    d="M1.5 12s4-7.5 10.5-7.5S22.5 12 22.5 12s-4 7.5-10.5 7.5S1.5 12 1.5 12z"
                    stroke="#475569"
                    strokeWidth="1.8"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  />
                  <circle cx="12" cy="12" r="3" stroke="#475569" strokeWidth="1.8" />
                </svg>
              )}
            </button>
          </div>

          {/* Link quên mật khẩu */}
          <div style={{ textAlign: "right", marginBottom: "18px" }}>
            <Link to="/forgot-password" style={styles.forgotLink}>
              Quên mật khẩu?
            </Link>
          </div>

          {/* Nút đăng nhập */}
          <button type="submit" className="btn-submit">
            Đăng nhập
          </button>
        </form>

        {/* Thông báo */}
        {message && (
          <p
            style={{
              color: message.includes("thành công") ? "#16a34a" : "#e74c3c",
              fontSize: "14px",
              marginTop: "14px",
              fontWeight: "500",
            }}
          >
            {message}
          </p>
        )}

        <p style={styles.footerText}>
          Chưa có tài khoản?{" "}
          <Link to="/signup" style={styles.link}>
            Đăng ký ngay
          </Link>
        </p>
      </div>

      {/* CSS nội tuyến */}
      <style>{`
        .input-field {
          width: 100%;
          padding: 14px 16px;
          margin-bottom: 15px;
          border: 1px solid #e0e7ff;
          border-radius: 14px;
          outline: none;
          font-size: 15px;
          transition: all 0.25s ease;
          box-sizing: border-box;
        }

        .input-field:focus {
          border-color: #6366f1;
          box-shadow: 0 0 6px rgba(99,102,241,0.4);
        }

        .btn-submit {
          width: 100%;
          padding: 13px 14px;
          background: linear-gradient(135deg, #4f46e5 0%, #3b82f6 100%);
          color: white;
          border: none;
          border-radius: 14px;
          font-size: 16px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.25s ease;
        }

        .btn-submit:hover {
          transform: translateY(-2px);
          box-shadow: 0 6px 16px rgba(79,70,229,0.3);
        }
      `}</style>
    </div>
  );
}

const styles = {
  container: {
    height: "100vh",
    background: "linear-gradient(135deg, #93c5fd 0%, #c7d2fe 100%)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontFamily: "'Poppins', sans-serif",
    padding: "20px",
  },
  card: {
    background: "#fff",
    padding: "40px 36px",
    borderRadius: "20px",
    boxShadow: "0 10px 30px rgba(17, 24, 39, 0.1)",
    width: "400px",
    textAlign: "center",
    animation: "fadeIn 0.5s ease-in-out",
  },
  title: {
    marginBottom: "22px",
    fontSize: "26px",
    fontWeight: "700",
    color: "#1e293b",
  },
  inputWrapper: {
    position: "relative",
    width: "100%",
    marginBottom: "10px",
  },
  iconButton: {
    position: "absolute",
    right: "12px",
    top: "50%",
    transform: "translateY(-50%)",
    background: "transparent",
    border: "none",
    cursor: "pointer",
    padding: "6px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  forgotLink: {
    fontSize: "14px",
    color: "#3b82f6",
    textDecoration: "none",
    fontWeight: "500",
  },
  footerText: {
    marginTop: "20px",
    color: "#555",
    fontSize: "14px",
  },
  link: {
    color: "#4f46e5",
    textDecoration: "none",
    fontWeight: "600",
  },
};

export default Login;
