import React, { useState, useEffect } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Login from "./Login";
import Signup from "./Signup";
import UserList from "./UserList";
import AddUser from "./AddUser";
import Profile from "./ProfileUser";
import { jwtDecode } from "jwt-decode"; // named import

function App() {
  const [refresh, setRefresh] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const handleAdd = () => setRefresh(!refresh);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    setCurrentUser(null);
    navigate("/"); // Quay lại trang đăng nhập
  };

  // Lấy thông tin user từ token khi App load
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setCurrentUser(decoded);
      } catch (err) {
        console.error("Token không hợp lệ:", err);
        localStorage.removeItem("token");
      }
    }
  }, []);

  return (
    <Routes>
      {/* Trang đăng nhập */}
      <Route path="/" element={<Login />} />

      {/* Trang đăng ký */}
      <Route path="/signup" element={<Signup />} />

      {/* Trang profile người dùng */}
      <Route path="/profile" element={<Profile />} />

      {/* Trang dashboard */}
      <Route
        path="/dashboard"
        element={
          <div style={styles.container}>
            {/* Header */}
            <div style={styles.header}>
              <h1 style={styles.title}>Quản lý người dùng</h1>
              {currentUser && (
                <div style={styles.userInfo}>
                  <span>Xin chào, <b>{currentUser.name || currentUser.email}</b></span>
                  <button style={styles.logoutButton} onClick={handleLogout}>
                    Đăng xuất
                  </button>
                </div>
              )}
            </div>

            <AddUser onAdd={handleAdd} />
            <UserList key={refresh} />
          </div>
        }
      />
    </Routes>
  );
}

const styles = {
  container: {
    position: "relative",
    fontFamily: "'Inter', sans-serif",
    padding: "30px",
  },
  header: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    position: "relative",
    marginBottom: "25px",
  },
  title: {
    textAlign: "center",
    fontSize: "28px",
    fontWeight: "700",
  },
  userInfo: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    position: "absolute",
    right: "20px",
    top: "20px",
  },
  logoutButton: {
    background: "linear-gradient(to right, #b91c1c, #f87171)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px",
    padding: "10px 20px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    transition: "all 0.25s ease",
  },
};

export default App;
