import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";

function UserList() {
  const [users, setUsers] = useState([]);
  const [editUser, setEditUser] = useState(null);
  const [form, setForm] = useState({ name: "", email: "" });

  // ‚úÖ D√πng useCallback ƒë·ªÉ tr√°nh warning missing dependency
  const fetchUsers = useCallback(async () => {
    try {
      const token = localStorage.getItem("token"); // ‚úÖ L·∫•y token ƒë√£ l∆∞u khi ƒëƒÉng nh·∫≠p
      const res = await axios.get("http://localhost:3000/users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(res.data);
    } catch (err) {
      console.error("‚ùå L·ªói t·∫£i danh s√°ch:", err.response?.data || err.message);
    }
  }, []);

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]); // ‚úÖ Kh√¥ng c√≤n c·∫£nh b√°o ESLint n·ªØa

  // ‚úÖ X√ìA user (c·∫≠p nh·∫≠t giao di·ªán ngay)
  const handleDelete = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:3000/users/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers((prevUsers) => prevUsers.filter((u) => u._id !== id));
    } catch (err) {
      console.error("‚ùå L·ªói khi x√≥a:", err.response?.data || err.message);
    }
  };

  // ‚úÖ B·∫Øt ƒë·∫ßu s·ª≠a
  const handleEdit = (user) => {
    setEditUser(user);
    setForm({ name: user.name, email: user.email });
  };

  // ‚úÖ L∆∞u c·∫≠p nh·∫≠t
  const handleUpdate = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.put(
        `http://localhost:3000/users/${editUser._id}`,
        form,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setUsers((prevUsers) =>
        prevUsers.map((u) => (u._id === editUser._id ? res.data : u))
      );

      setEditUser(null);
    } catch (err) {
      console.error("‚ùå C·∫≠p nh·∫≠t l·ªói:", err.response?.data || err.message);
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>üë• Danh s√°ch ng∆∞·ªùi d√πng</h2>

      <div style={styles.cardContainer}>
        {users.length > 0 ? (
          users.map((user) => (
            <div key={user._id} style={styles.card}>
              <h3 style={styles.name}>{user.name}</h3>
              <p style={styles.email}>{user.email}</p>
              <div style={styles.actions}>
                <button style={styles.editBtn} onClick={() => handleEdit(user)}>
                  ‚úèÔ∏è S·ª≠a
                </button>
                <button
                  style={styles.deleteBtn}
                  onClick={() => handleDelete(user._id)}
                >
                  üóëÔ∏è X√≥a
                </button>
              </div>
            </div>
          ))
        ) : (
          <p style={{ textAlign: "center" }}>Kh√¥ng c√≥ ng∆∞·ªùi d√πng n√†o.</p>
        )}
      </div>

      {editUser && (
        <div style={styles.editForm}>
          <h3 style={styles.formTitle}>‚úèÔ∏è Ch·ªânh s·ª≠a ng∆∞·ªùi d√πng</h3>
          <input
            style={styles.input}
            type="text"
            placeholder="T√™n"
            value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })}
          />
          <input
            style={styles.input}
            type="email"
            placeholder="Email"
            value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })}
          />
          <div>
            <button style={styles.saveBtn} onClick={handleUpdate}>
              üíæ L∆∞u
            </button>
            <button
              style={styles.cancelBtn}
              onClick={() => setEditUser(null)}
            >
              ‚ùå H·ªßy
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    padding: "30px",
    maxWidth: "900px",
    margin: "auto",
  },
  title: {
    textAlign: "center",
    color: "#34495e",
    marginBottom: "25px",
  },
  cardContainer: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(220px, 1fr))",
    gap: "20px",
  },
  card: {
    backgroundColor: "#f9f9f9",
    borderRadius: "12px",
    padding: "20px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
  },
  name: {
    color: "#2c3e50",
    marginBottom: "5px",
  },
  email: {
    color: "#7f8c8d",
    fontSize: "14px",
  },
  actions: {
    marginTop: "10px",
    display: "flex",
    justifyContent: "space-between",
  },
  editBtn: {
    backgroundColor: "#f1c40f",
    border: "none",
    borderRadius: "8px",
    padding: "6px 12px",
    cursor: "pointer",
    color: "#fff",
    fontWeight: "bold",
  },
  deleteBtn: {
    backgroundColor: "#e74c3c",
    border: "none",
    borderRadius: "8px",
    padding: "6px 12px",
    cursor: "pointer",
    color: "#fff",
    fontWeight: "bold",
  },
  editForm: {
    marginTop: "40px",
    padding: "20px",
    backgroundColor: "#fff",
    borderRadius: "12px",
    boxShadow: "0 2px 10px rgba(0,0,0,0.15)",
    textAlign: "center",
  },
  formTitle: {
    color: "#2c3e50",
    marginBottom: "15px",
  },
  input: {
    display: "block",
    width: "100%",
    marginBottom: "10px",
    padding: "10px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    fontSize: "15px",
  },
  saveBtn: {
    backgroundColor: "#2ecc71",
    border: "none",
    borderRadius: "8px",
    padding: "8px 14px",
    marginRight: "10px",
    cursor: "pointer",
    color: "#fff",
    fontWeight: "bold",
  },
  cancelBtn: {
    backgroundColor: "#7f8c8d",
    border: "none",
    borderRadius: "8px",
    padding: "8px 14px",
    cursor: "pointer",
    color: "#fff",
    fontWeight: "bold",
  },
};

export default UserList;
