import React, { useState, useEffect } from "react";
import { Routes, Route, useNavigate, Navigate } from "react-router-dom";
import Login from "./Login";
import Signup from "./Signup";
import UserList from "./UserList";
import AddUser from "./AddUser";
import Profile from "./ProfileUser";
import ForgotPassword from "./ForgotPassword";
import ResetPassword from "./ResetPassword";
import UploadAvatar from "./UploadAvatar";
import { jwtDecode } from "jwt-decode";
import api from "./api"; // ‚úÖ interceptor

function App() {
  const [refresh, setRefresh] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleAdd = () => setRefresh(!refresh);

  // ---------------------------------------------------------
  // üîπ X·ª¨ L√ù ƒêƒÇNG XU·∫§T
  // ---------------------------------------------------------
  const handleLogout = async () => {
    const refreshToken = localStorage.getItem("refreshToken");
    try {
      if (refreshToken) {
        await api.post("/auth/logout", { refreshToken });
      }
    } catch (err) {
      console.error("L·ªói khi logout tr√™n server:", err);
    } finally {
      localStorage.removeItem("accessToken");
      localStorage.removeItem("refreshToken");
      localStorage.removeItem("user");
      setCurrentUser(null);
      navigate("/");
    }
  };

  // ---------------------------------------------------------
  // üîπ LOAD USER T·ª™ LOCALSTORAGE KHI M·ªû APP
  // ---------------------------------------------------------
  useEffect(() => {
    const token = localStorage.getItem("accessToken");
    const userJson = localStorage.getItem("user");

    if (token && userJson) {
      try {
        const decoded = jwtDecode(token);
        const userData = JSON.parse(userJson);
        userData.role = decoded.role; // ƒë·ªìng b·ªô role t·ª´ token
        setCurrentUser(userData);

        const now = Date.now() / 1000;
        if (decoded.exp < now) {
          console.warn("Access Token h·∫øt h·∫°n, interceptor s·∫Ω x·ª≠ l√Ω.");
        }
      } catch (err) {
        console.error("Token ho·∫∑c user l·ªói:", err);
        localStorage.clear();
      }
    }
    setLoading(false);
  }, []);

  // ---------------------------------------------------------
  // üîπ C√ÅC ROUTE B·∫¢O V·ªÜ
  // ---------------------------------------------------------
  const ProtectedRoute = ({ children }) => {
    if (loading) return <div>ƒêang t·∫£i...</div>;
    if (!currentUser) return <Navigate to="/" replace />;
    return children;
  };

  const RoleRoute = ({ children, allow }) => {
    if (loading) return <div>ƒêang t·∫£i...</div>;
    if (!currentUser) return <Navigate to="/" replace />;
    if (!allow.includes(currentUser.role))
      return <Navigate to="/profile" replace />;
    return children;
  };

  // ---------------------------------------------------------
  // üîπ RENDER GIAO DI·ªÜN THEO ROLE
  // ---------------------------------------------------------
  const renderDashboard = () => {
    if (currentUser.role === "admin") {
      return (
        <div style={styles.container}>
          <div style={styles.header}>
            <h1 style={styles.title}>Qu·∫£n l√Ω ng∆∞·ªùi d√πng (Admin)</h1>
            <div style={styles.userInfo}>
              <span>
                Xin ch√†o, <b>{currentUser.name || currentUser.email}</b>
              </span>
              <button style={styles.logoutButton} onClick={handleLogout}>
                ƒêƒÉng xu·∫•t
              </button>
            </div>
          </div>
          <AddUser onAdd={handleAdd} />
          <UserList key={refresh} />
        </div>
      );
    }

    if (currentUser.role === "moderator") {
      return (
        <div style={styles.container}>
          <h1 style={styles.title}>Trang ƒëi·ªÅu h√†nh (Moderator)</h1>
          <p>B·∫°n c√≥ th·ªÉ duy·ªát b√†i vi·∫øt ho·∫∑c qu·∫£n l√Ω n·ªôi dung ng∆∞·ªùi d√πng.</p>
          <button style={styles.logoutButton} onClick={handleLogout}>
            ƒêƒÉng xu·∫•t
          </button>
        </div>
      );
    }

    return (
      <div style={styles.container}>
        <h1 style={styles.title}>Trang ng∆∞·ªùi d√πng (User)</h1>
        <p>B·∫°n ch·ªâ c√≥ th·ªÉ xem n·ªôi dung v√† ch·ªânh s·ª≠a h·ªì s∆° c√° nh√¢n.</p>
        <button style={styles.logoutButton} onClick={handleLogout}>
          ƒêƒÉng xu·∫•t
        </button>
      </div>
    );
  };

  // ---------------------------------------------------------
  // üîπ GIAO DI·ªÜN CH√çNH
  // ---------------------------------------------------------
  if (loading) return <div>ƒêang t·∫£i ·ª©ng d·ª•ng...</div>;

  return (
    <Routes>
      {/* Public routes */}
      <Route path="/" element={<Login setCurrentUser={setCurrentUser} />} />
      <Route path="/signup" element={<Signup />} />
      <Route path="/forgot-password" element={<ForgotPassword />} />
      <Route path="/reset-password/:token" element={<ResetPassword />} />
      <Route path="/reset-password" element={<ResetPassword />} />

      {/* User routes */}
      <Route
        path="/profile"
        element={
          <ProtectedRoute>
            <Profile currentUser={currentUser} onLogout={handleLogout} />
          </ProtectedRoute>
        }
      />
      <Route
        path="/upload-avatar"
        element={
          <ProtectedRoute>
            <UploadAvatar />
          </ProtectedRoute>
        }
      />

      {/* Dashboard - hi·ªÉn th·ªã theo vai tr√≤ */}
      <Route
        path="/dashboard"
        element={
          <ProtectedRoute>
            {renderDashboard()}
          </ProtectedRoute>
        }
      />
    </Routes>
  );
}

// ---------------------------------------------------------
// üîπ STYLE
// ---------------------------------------------------------
const styles = {
  container: {
    position: "relative",
    fontFamily: "'Inter', sans-serif",
    padding: "30px",
    textAlign: "center",
  },
  header: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    position: "relative",
    marginBottom: "25px",
  },
  title: { fontSize: "28px", fontWeight: "700" },
  userInfo: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    position: "absolute",
    right: "20px",
    top: "20px",
  },
  logoutButton: {
    background: "linear-gradient(to right, #b91c1c, #f87171)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px",
    padding: "10px 20px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    transition: "all 0.25s ease",
  },
};

export default App;
