// src/App.js
import './App.css';
import React, { useState, useEffect } from "react";
import UserList from "./UserList";
import AddUser from "./AddUser";
import Login from "./Login";
import Signup from "./Signup";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showSignup, setShowSignup] = useState(false);
  const [refresh, setRefresh] = useState(false);

  // üîÑ Gi·ªØ tr·∫°ng th√°i ƒëƒÉng nh·∫≠p sau khi refresh trang
  useEffect(() => {
    const savedLogin = localStorage.getItem("isLoggedIn");
    if (savedLogin === "true") {
      setIsLoggedIn(true);
    }
  }, []);

  const handleAdd = () => setRefresh(!refresh);
  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem("isLoggedIn", "true");
  };
  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem("isLoggedIn");
  };

  // üîê N·∫øu ch∆∞a ƒëƒÉng nh·∫≠p ‚Üí Hi·ªán Login ho·∫∑c Signup
  if (!isLoggedIn) {
    return showSignup ? (
      <Signup
        onSignup={() => setShowSignup(false)}
        onSwitchToLogin={() => setShowSignup(false)}
      />
    ) : (
      <Login
        onLogin={handleLogin}
        onSwitchToSignup={() => setShowSignup(true)}
      />
    );
  }

  // üë• N·∫øu ƒë√£ ƒëƒÉng nh·∫≠p ‚Üí Hi·ªán giao di·ªán qu·∫£n l√Ω ng∆∞·ªùi d√πng
  return (
    <div className="App">
      <h1>Qu·∫£n l√Ω ng∆∞·ªùi d√πng</h1>
      <button onClick={handleLogout} style={styles.logout}>ƒêƒÉng xu·∫•t</button>
      <AddUser onAdd={handleAdd} />
      <UserList key={refresh} />
    </div>
  );
}

const styles = {
  logout: {
    position: "absolute",
    top: "20px",
    right: "20px",
    backgroundColor: "#dc3545",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    padding: "8px 12px",
    cursor: "pointer",
  },
};

export default App;
