import React, { useState, useEffect } from "react";
import { Routes, Route, useNavigate, Navigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import { useDispatch } from "react-redux";
import {
  loginSuccess,
  logout as logoutAction,
} from "./redux/authSlice";

import api from "./api";
import Login from "./Login";
import Signup from "./Signup";
import UserList from "./UserList";
import AddUser from "./AddUser";
import Profile from "./ProfileUser";
import ForgotPassword from "./ForgotPassword";
import ResetPassword from "./ResetPassword";
import UploadAvatar from "./UploadAvatar";
import AdminLogs from "./AdminLogs";
import ProtectedRoute from "./ProtectedRoute";
import RoleRoute from "./RoleRoute";

function App() {
  const [refresh, setRefresh] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const handleAdd = () => setRefresh(!refresh);

  // üßπ ƒêƒÉng xu·∫•t
  const handleLogout = async () => {
    const refreshToken = localStorage.getItem("refreshToken");
    try {
      if (refreshToken) await api.post("/auth/logout", { refreshToken });
    } catch (err) {
      console.error("L·ªói khi logout:", err);
    } finally {
      ["accessToken", "refreshToken", "user"].forEach((key) =>
        localStorage.removeItem(key)
      );
      setCurrentUser(null);
      dispatch(logoutAction());
      navigate("/");
    }
  };

  // üöÄ T·∫£i user v√† ki·ªÉm tra token khi m·ªü trang
  useEffect(() => {
    const token = localStorage.getItem("accessToken");
    const userJson = localStorage.getItem("user");

    if (token && userJson) {
      try {
        const userData = JSON.parse(userJson);
        setCurrentUser(userData);
        dispatch(
          loginSuccess({
            user: userData,
            accessToken: token,
            refreshToken: localStorage.getItem("refreshToken"),
          })
        );

        // Ki·ªÉm tra token h·∫øt h·∫°n
        const decoded = jwtDecode(token);
        const now = Date.now() / 1000;
        if (decoded.exp < now) {
          console.warn("Access token ƒë√£ h·∫øt h·∫°n, interceptor s·∫Ω t·ª± refresh.");
        }
      } catch (err) {
        console.error("Token/user l·ªói:", err);
        ["accessToken", "refreshToken", "user"].forEach((key) =>
          localStorage.removeItem(key)
        );
      }
    }
    setLoading(false);
  }, [dispatch]);

  if (loading)
    return (
      <div style={{ textAlign: "center", marginTop: "20%" }}>
        <p>‚è≥ ƒêang t·∫£i d·ªØ li·ªáu ng∆∞·ªùi d√πng...</p>
      </div>
    );

  return (
    <Routes>
      {/* --- C√¥ng khai --- */}
      <Route path="/" element={<Login setCurrentUser={setCurrentUser} />} />
      <Route path="/signup" element={<Signup />} />
      <Route path="/forgot-password" element={<ForgotPassword />} />
      <Route path="/reset-password/:token" element={<ResetPassword />} />

      {/* --- Ng∆∞·ªùi d√πng ƒë√£ login --- */}
      <Route
        path="/profile"
        element={
          <ProtectedRoute currentUser={currentUser} loading={loading}>
            <Profile currentUser={currentUser} onLogout={handleLogout} />
          </ProtectedRoute>
        }
      />
      <Route
        path="/upload-avatar"
        element={
          <ProtectedRoute currentUser={currentUser} loading={loading}>
            <UploadAvatar />
          </ProtectedRoute>
        }
      />

      {/* --- Dashboard cho admin & moderator --- */}
      <Route
        path="/dashboard"
        element={
          <RoleRoute
            currentUser={currentUser}
            loading={loading}
            allowedRoles={["admin", "moderator"]}
          >
            <div style={styles.container}>
              <div style={styles.header}>
                {currentUser && (
                  <div style={styles.userInfo}>
                    <span>
                      Xin ch√†o, <b>{currentUser.name || currentUser.email}</b> (
                      {currentUser.role})
                    </span>

                    {currentUser.role === "admin" && (
                      <button
                        style={styles.logButton}
                        onClick={() => navigate("/logs")}
                      >
                        Nh·∫≠t k√Ω
                      </button>
                    )}

                    <button
                      style={styles.profileButton}
                      onClick={() => navigate("/profile")}
                    >
                      Trang c√° nh√¢n
                    </button>

                    <button
                      style={styles.logoutButton}
                      onClick={handleLogout}
                    >
                      ƒêƒÉng xu·∫•t
                    </button>
                  </div>
                )}

                <h1 style={styles.title}>
                  {currentUser?.role === "admin" ? (
                    <>
                      Qu·∫£n l√Ω ng∆∞·ªùi d√πng <br /> (Admin)
                    </>
                  ) : (
                    <>
                      B·∫£ng ƒëi·ªÅu khi·ªÉn <br /> (Moderator)
                    </>
                  )}
                </h1>
              </div>

              {currentUser?.role === "admin" && <AddUser onAdd={handleAdd} />}
              <UserList key={refresh} canEdit={currentUser?.role === "admin"} />
            </div>
          </RoleRoute>
        }
      />

      {/* --- Trang log cho Admin --- */}
      <Route
        path="/logs"
        element={
          <RoleRoute
            currentUser={currentUser}
            loading={loading}
            allowedRoles={["admin"]}
          >
            <AdminLogs />
          </RoleRoute>
        }
      />

      {/* --- Trang m·∫∑c ƒë·ªãnh --- */}
      <Route path="*" element={<Navigate to="/" replace />} />
    </Routes>
  );
}

// --- üé® STYLE ---
const styles = {
  container: {
    position: "relative",
    fontFamily: "'Inter', sans-serif",
    padding: "30px",
  },
  header: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    position: "relative",
    marginBottom: "25px",
  },
  title: {
    textAlign: "center",
    fontSize: "28px",
    fontWeight: "700",
    marginTop: "50px",
  },
  userInfo: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    gap: "10px",
    position: "absolute",
    top: "0",
    right: "20px",
  },
  logButton: {
    background: "linear-gradient(to right, #059669, #34d399)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px",
    padding: "10px 18px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
    transition: "all 0.25s ease",
  },
  profileButton: {
    background: "linear-gradient(to right, #2563eb, #60a5fa)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px",
    padding: "10px 18px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
    transition: "all 0.25s ease",
  },
  logoutButton: {
    background: "linear-gradient(to right, #b91c1c, #f87171)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px",
    padding: "10px 20px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    transition: "all 0.25s ease",
  },
};

export default App;
