import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function AdminLogs() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("http://localhost:3000/api/logs")
      .then((res) => setLogs(res.data))
      .catch((err) => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <button
          style={styles.backButton}
          onClick={() => navigate("/dashboard")}
        >
          Quay l·∫°i Dashboard
        </button>

        <h2 style={styles.title}>Nh·∫≠t k√Ω ho·∫°t ƒë·ªông ng∆∞·ªùi d√πng</h2>

        {loading ? (
          <p>ƒêang t·∫£i d·ªØ li·ªáu...</p>
        ) : logs.length === 0 ? (
          <p>Kh√¥ng c√≥ log ho·∫°t ƒë·ªông n√†o.</p>
        ) : (
          <table style={styles.table}>
            <thead>
              <tr>
                <th>Ng∆∞·ªùi d√πng</th>
                <th>H√†nh ƒë·ªông</th>
                <th>Th·ªùi gian</th>
              </tr>
            </thead>
            <tbody>
              {logs.map((log) => (
                <tr key={log._id}>
                  <td>{log.user?.name || "·∫®n danh"}</td>
                  <td>{log.action}</td>
                  <td>{new Date(log.timestamp).toLocaleString("vi-VN")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: { padding: "30px", fontFamily: "'Inter', sans-serif" },
  card: {
    background: "#fff",
    borderRadius: "20px",
    padding: "30px",
    maxWidth: "900px",
    margin: "0 auto",
    boxShadow: "0 8px 25px rgba(0,0,0,0.15)",
  },
  backButton: {
    background: "linear-gradient(to right, #2563eb, #60a5fa)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px", // üîπ bo tr√≤n
    padding: "10px 20px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
    marginBottom: "20px",
  },
  title: { textAlign: "center", fontSize: "26px", fontWeight: "700" },
  table: { width: "100%", borderCollapse: "collapse" },
