import React, { useState, useEffect } from "react";
import { Routes, Route, useNavigate, Navigate } from "react-router-dom";
import Login from "./Login";
import Signup from "./Signup";
import UserList from "./UserList";
import AddUser from "./AddUser";
import Profile from "./ProfileUser";
import ForgotPassword from "./ForgotPassword";
import ResetPassword from "./ResetPassword";
import UploadAvatar from "./UploadAvatar";
import Dashboard from "./UserList"; // ‚úÖ trang dashboard demo role
import { jwtDecode } from "jwt-decode";
import api from "./api";

function App() {
  const [refresh, setRefresh] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const handleAdd = () => setRefresh(!refresh);

  // üß© Logout
  const handleLogout = async () => {
    const refreshToken = localStorage.getItem("refreshToken");
    try {
      if (refreshToken) await api.post("/auth/logout", { refreshToken });
    } catch (err) {
      console.error("L·ªói khi logout:", err);
    } finally {
      localStorage.removeItem("accessToken");
      localStorage.removeItem("refreshToken");
      localStorage.removeItem("user");
      setCurrentUser(null);
      navigate("/");
    }
  };

  // üß© Load user t·ª´ localStorage
  useEffect(() => {
    const token = localStorage.getItem("accessToken");
    const userJson = localStorage.getItem("user");

    if (token && userJson) {
      try {
        const userData = JSON.parse(userJson);
        setCurrentUser(userData);

        const decoded = jwtDecode(token);
        const now = Date.now() / 1000;
        if (decoded.exp < now) {
          console.warn("Access token ƒë√£ h·∫øt h·∫°n, interceptor s·∫Ω t·ª± refresh.");
        }
      } catch (err) {
        console.error("Token/user l·ªói:", err);
        localStorage.removeItem("accessToken");
        localStorage.removeItem("refreshToken");
        localStorage.removeItem("user");
      }
    }
    setLoading(false);
  }, []);

  // üß© Route ki·ªÉm tra quy·ªÅn theo role
  const RoleRoute = ({ allowedRoles, children }) => {
    if (loading) return <div>ƒêang t·∫£i...</div>;
    if (!currentUser) return <Navigate to="/" replace />;

    if (!allowedRoles.includes(currentUser.role)) {
      return <Navigate to="/profile" replace />;
    }
    return children;
  };

  // üß© Route y√™u c·∫ßu ƒëƒÉng nh·∫≠p (b·∫•t k·ª≥ role)
  const ProtectedRoute = ({ children }) => {
    if (loading) return <div>ƒêang t·∫£i...</div>;
    if (!currentUser) return <Navigate to="/" replace />;
    return children;
  };

  if (loading) return <div>ƒêang t·∫£i ·ª©ng d·ª•ng...</div>;

  return (
    <Routes>
      {/* --- C√¥ng khai --- */}
      <Route path="/" element={<Login setCurrentUser={setCurrentUser} />} />
      <Route path="/signup" element={<Signup />} />
      <Route path="/forgot-password" element={<ForgotPassword />} />
      <Route path="/reset-password/:token" element={<ResetPassword />} />

      {/* --- Ng∆∞·ªùi d√πng ƒë√£ login --- */}
      <Route
        path="/profile"
        element={
          <ProtectedRoute>
            <Profile currentUser={currentUser} onLogout={handleLogout} />
          </ProtectedRoute>
        }
      />
      <Route
        path="/upload-avatar"
        element={
          <ProtectedRoute>
            <UploadAvatar />
          </ProtectedRoute>
        }
      />

      {/* --- RBAC n√¢ng cao --- */}
      <Route
        path="/dashboard"
        element={
          <RoleRoute allowedRoles={["admin"]}>
            <div style={styles.container}>
              <div style={styles.header}>
                <h1 style={styles.title}>Qu·∫£n l√Ω ng∆∞·ªùi d√πng (Admin)</h1>
                {currentUser && (
                  <div style={styles.userInfo}>
                    <span>
                      Xin ch√†o, <b>{currentUser.name || currentUser.email}</b> (
                      {currentUser.role})
                    </span>
                    <button style={styles.logoutButton} onClick={handleLogout}>
                      ƒêƒÉng xu·∫•t
                    </button>
                  </div>
                )}
              </div>

              <AddUser onAdd={handleAdd} />
              <UserList key={refresh} />
            </div>
          </RoleRoute>
        }
      />

      <Route
        path="/moderator"
        element={
          <RoleRoute allowedRoles={["moderator", "admin"]}>
            <Dashboard />
          </RoleRoute>
        }
      />

      <Route
        path="/user-home"
        element={
          <RoleRoute allowedRoles={["user", "admin", "moderator"]}>
            <Dashboard />
          </RoleRoute>
        }
      />
    </Routes>
  );
}

// --- Style ---
const styles = {
  container: {
    position: "relative",
    fontFamily: "'Inter', sans-serif",
    padding: "30px",
  },
  header: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    position: "relative",
    marginBottom: "25px",
  },
  title: { textAlign: "center", fontSize: "28px", fontWeight: "700" },
  userInfo: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
    position: "absolute",
    right: "20px",
    top: "20px",
  },
  logoutButton: {
    background: "linear-gradient(to right, #b91c1c, #f87171)",
    color: "#fff",
    border: "none",
    borderRadius: "9999px",
    padding: "10px 20px",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    boxShadow: "0 2px 8px rgba(0,0,0,0.15)",
    transition: "all 0.25s ease",
  },
};

export default App;
