import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

function ResetPassword() {
  const { token } = useParams();
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`http://localhost:3000/api/reset-password/${token}`, { password });
      setMessage(res.data.message || "‚úÖ M·∫≠t kh·∫©u ƒë√£ ƒë∆∞·ª£c ƒë·∫∑t l·∫°i th√†nh c√¥ng!");
    } catch (error) {
      setMessage("‚ùå Token kh√¥ng h·ª£p l·ªá ho·∫∑c ƒë√£ h·∫øt h·∫°n!");
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <h2 style={styles.title}>üîí ƒê·∫∑t l·∫°i m·∫≠t kh·∫©u</h2>

        <form onSubmit={handleSubmit} style={{ textAlign: "center" }}>
          <div style={{ position: "relative" }}>
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Nh·∫≠p m·∫≠t kh·∫©u m·ªõi"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={styles.input}
            />
            <span
              onClick={() => setShowPassword(!showPassword)}
              style={styles.eyeIcon}
            >
              {showPassword ? "üôà" : "üëÅÔ∏è"}
            </span>
          </div>

          <button type="submit" style={styles.button}>
            C·∫≠p nh·∫≠t m·∫≠t kh·∫©u
          </button>
        </form>

        {message && (
          <p
            style={{
              color: message.includes("‚úÖ") ? "#16a34a" : "#dc2626",
              marginTop: "15px",
              fontWeight: "500",
              fontSize: "15px",
            }}
          >
            {message}
          </p>
        )}

        <p style={{ marginTop: "20px", fontSize: "14px" }}>
          <Link to="/" style={styles.link}>‚Üê Quay l·∫°i ƒëƒÉng nh·∫≠p</Link>
        </p>
      </div>
    </div>
  );
}

const styles = {
  container: {
    height: "100vh",
    background: "linear-gradient(135deg, #c2e9fb 0%, #a1c4fd 100%)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontFamily: "Arial, sans-serif",
  },
  card: {
    background: "#fff",
    padding: "40px 30px",
    borderRadius: "20px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.1)",
    width: "380px",
    textAlign: "center",
  },
  title: {
    marginBottom: "25px",
    fontSize: "24px",
    fontWeight: "600",
    color: "#111827",
  },
  input: {
    width: "100%",
    padding: "14px 16px",
    marginBottom: "20px",
    borderRadius: "12px",
    border: "1px solid #d1d5db",
    fontSize: "15px",
    outline: "none",
    boxSizing: "border-box",
  },
  button: {
    width: "100%",
    padding: "12px 14px",
    background: "linear-gradient(135deg, #4f46e5 0%, #3b82f6 100%)",
    color: "#fff",
    border: "none",
    borderRadius: "12px",
    fontSize: "16px",
    fontWeight: "600",
    cursor: "pointer",
    transition: "0.2s",
  },
  link: {
    color: "#2563eb",
    textDecoration: "none",
  },
  eyeIcon: {
    position: "absolute",
    right: "15px",
    top: "50%",
    transform: "translateY(-50%)",
    cursor: "pointer",
    fontSize: "18px",
  },
};

export default ResetPassword;
